import React, { useMemo } from "react";
import { useReactTable } from '@tanstack/react-table'
import planets from '../server/database/data.json'
import { COLUMNS } from "./columns";
import './table.css';

export const BasicTable = () => {
    // Мемоизация колонок и данных для оптимизации производительности
    const columns = useMemo(() => COLUMNS, []);
    const data = useMemo(() => planets, []);
  
    // Используем useTable вместо useReactTable для корректного импорта
    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useReactTable({
      columns,
      data,
    });
  
    return (
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render('header')}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              // Исправляем getRowProps на row.getRowProps
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    );
};
