import React, { useState} from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Spinner } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Context } from '../index';
import { NavLink, useNavigate } from 'react-router-dom';
import {ADMIN_ROUTE, CART_ROUTE, LOGIN_ROUTE, SHOP_ROUTE } from '../utils/consts';
import { observer } from "mobx-react";

const NavBar = observer( () => {
  const loading = useState(true)
  const navigate = useNavigate()
  
  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
        <Spinner animation={"grow"} />
      </div>
    );
  }

  return (
    <>
      <Navbar bg="dark" data-bs-theme="dark" sticky='top'>
        <Container>
          <NavLink style={{ color: 'whitesmoke', textDecoration: 'none' }} to={SHOP_ROUTE} className="me-2">
            Супер магазин
          </NavLink>
          {user.isAuth &&
            <Nav className="me-auto">
              <NavLink style={{ color: 'whitesmoke', textDecoration: 'none' }} to={CART_ROUTE}>
                Корзина
              </NavLink>
            </Nav>
          }
        </Container>
        <Form>
          <Row className="align-items-center">
            {user.isAuth ? (
              user.user.role === 'Admin' ? (
                <Col className='d-flex align-items-center ' md="auto">
                  <div className="text-white fs-5 me-4">
                    Баланс: {user.balance} ₽
                  </div>
                  <Button
                    variant={"outline-light"}
                    onClick={() => navigate(ADMIN_ROUTE)}
                    className="me-2" 
                  >
                    Админ панель
                  </Button>
                  <Button
                    variant={"outline-light"}
                    onClick={() => logOut()}
                    className="me-2" 
                  >
                    Выход
                  </Button>
                </Col>
              ) : (
                <Col className='d-flex align-items-center' md="auto">
                  <Navbar.Text className='text-white fs-5 me-4'>
                    Баланс: {user.balance} ₽
                  </Navbar.Text>
                  <Button
                    variant={"outline-light"}
                    onClick={() => logOut()}
                    className="me-2" 
                  >
                    Выход
                  </Button>
                </Col>
              )
            ) : (
              <Col xs="auto">
                <Button
                  variant={"outline-light"}
                  onClick={() => navigate(LOGIN_ROUTE)}
                  className="me-2" 
                >
                  Авторизация
                </Button>
              </Col>
            )}
          </Row>
        </Form>
      </Navbar>
    </>
  );
  
});

export default NavBar
