import React, { useState} from 'react';
import { Nav, Navbar, NavLink, Container } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { MAIN_ROUTE, TABLE_ROUTE } from '../utils/consts';
import { observer } from "mobx-react-lite";

const NavBar = observer( () => {
//   const loading = useState(true)
//   const navigate = useNavigate()
  
//   if (loading) {
//     return (
//       <div className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
//         <Spinner animation={"grow"} />
//       </div>
//     );
//   }

  return (
    <>
      <Navbar bg="dark" data-bs-theme="dark" sticky='top'>
        <Container>
          <NavLink style={{ color: 'whitesmoke', textDecoration: 'none' }} to={MAIN_ROUTE} className="me-2">
            Главная
          </NavLink>
            {/* <Nav className="me-auto"> */}
              <NavLink style={{ color: 'whitesmoke', textDecoration: 'none' }} to={TABLE_ROUTE}>
                Таблица
              </NavLink>
            {/* </Nav> */}
        </Container>
      </Navbar>
    </>
  );
  
});

export default NavBar
