/**
 * DevExtreme (esm/__internal/pagination/pages/page.js)
 * Version: 24.2.7
 * Build date: Mon Apr 28 2025
 *
 * Copyright (c) 2012 - 2025 Developer Express Inc. ALL RIGHTS RESERVED
 * Read about DevExtreme licensing here: https://js.devexpress.com/Licensing/
 */
import {
    createComponentVNode
} from "inferno";
import {
    BaseInfernoComponent
} from "@devextreme/runtime/inferno";
import {
    format
} from "../../../core/utils/string";
import {
    combineClasses
} from "../../core/r1/utils/render_utils";
import {
    PAGINATION_PAGE_CLASS,
    PAGINATION_SELECTION_CLASS
} from "../common/consts";
import {
    LightButton
} from "../common/light_button";
import {
    getLocalizationMessage
} from "../utils/compatibility_utils";
export const PageDefaultProps = {
    index: 0,
    selected: false,
    className: PAGINATION_PAGE_CLASS
};
export class Page extends BaseInfernoComponent {
    constructor() {
        super(...arguments);
        this.state = {};
        this.refs = null
    }
    getLabel() {
        return format(getLocalizationMessage(this.context, "dxPagination-page"), this.getValue())
    }
    getValue() {
        return this.props.index + 1
    }
    getClassName() {
        return combineClasses({
            [`${this.props.className}`]: !!this.props.className,
            [PAGINATION_SELECTION_CLASS]: !!this.props.selected
        })
    }
    render() {
        return createComponentVNode(2, LightButton, {
            className: this.getClassName(),
            label: this.getLabel(),
            onClick: this.props.onClick,
            selected: this.props.selected,
            children: this.getValue()
        })
    }
}
Page.defaultProps = PageDefaultProps;
